mapper.insert:
INSERT INTO vehicles (
    driver_id, plate_number, brand, model, color, year, seats,
    vehicle_type, fuel_type, insurance_number, insurance_expiry,
    inspection_expiry, status, vehicle_image, is_active, created_at, updated_at
) VALUES (
    #{driverId}, #{plateNumber}, #{brand}, #{model}, #{color}, #{year}, #{seats},
    #{vehicleType}, #{fuelType}, #{insuranceNumber}, #{insuranceExpiry},
    #{inspectionExpiry}, #{status}, #{vehicleImage}, #{isActive}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE vehicles
driver_id = #{driverId}, plate_number = #{plateNumber}, brand = #{brand}, model = #{model}, color = #{color}, year = #{year}, seats = #{seats}, vehicle_type = #{vehicleType}, fuel_type = #{fuelType}, insurance_number = #{insuranceNumber}, insurance_expiry = #{insuranceExpiry}, inspection_expiry = #{inspectionExpiry}, vehicle_image = #{vehicleImage}, status = #{status}, is_active = #{isActive}, updated_at = #{updatedAt},
WHERE id = #{id}

UPDATE vehicles 
SET is_active = FALSE, updated_at = CURRENT_TIMESTAMP
WHERE driver_id = #{driverId}
